mapper.select:
SELECT m1.column_id AS id, ( CASE WHEN (p.column_id = 0 OR p.column_id IS NULL) THEN 0 ELSE p.column_id END ) AS pId, m1.column_name AS name, ( CASE WHEN (p.column_id = 0 OR p.column_id IS NULL) THEN 'true' ELSE 'false' END ) as "open" FROM m_column m1 LEFT join m_column p ON m1.parent_id = p.column_id where m1.del_flag = 'N'
and m1.plaza_id = #{plazaId}
ORDER BY m1.sort ASC
